import React, { useState, useEffect } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
import {
    Card,
    CardHeader,
    CardBody,
    CardTitle,
    Table,
    Row,
    Col,
    Button,
    Badge,
    Alert,
} from "reactstrap";

function Reports() {
    const [reports, setReports] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [selectedReport, setSelectedReport] = useState(null);
    const [searchParams] = useSearchParams();
    const navigate = useNavigate();
    const reportId = searchParams.get("report_id");

    useEffect(() => {
        if (reportId) {
            fetchReportById(parseInt(reportId));
        } else {
            // Redirect to most recent report (512) when no report_id specified
            navigate("/reports?report_id=512", { replace: true });
        }
    }, [reportId, navigate]);

    const fetchReports = async (page = 1) => {
        try {
            setLoading(true);
            const response = await fetch(`/api/reports.php?page=${page}&limit=20`);
            const data = await response.json();
            if (data.success) {
                setReports(data.data);
                setTotalPages(data.pagination.pages);
                setSelectedReport(null);
            }
        } catch (error) {
            console.error("Error fetching reports:", error);
        } finally {
            setLoading(false);
        }
    };

    const fetchReportById = async (id) => {
        try {
            setLoading(true);
            const response = await fetch(`/api/reports.php?id=${id}`);
            const data = await response.json();
            if (data.success) {
                setSelectedReport(data.data);
            } else {
                setSelectedReport(null);
            }
        } catch (error) {
            console.error("Error fetching report:", error);
            setSelectedReport(null);
        } finally {
            setLoading(false);
        }
    };

    const handleViewReport = (id) => {
        navigate(`/reports?report_id=${id}`);
    };

    const handleBackToList = () => {
        navigate("/reports");
        setCurrentPage(1);
    };

    const handlePreviousReport = () => {
        const currentId = parseInt(reportId);
        if (currentId > 1) {
            navigate(`/reports?report_id=${currentId - 1}`);
        }
    };

    const handleNextReport = () => {
        const currentId = parseInt(reportId);
        if (currentId < 512) {
            navigate(`/reports?report_id=${currentId + 1}`);
        }
    };

    if (loading) {
        return (
            <div className="content">
                <div className="text-center">Loading...</div>
            </div>
        );
    }

    // Show individual report view
    if (reportId && selectedReport) {
        return (
            <div className="content">
                <Row>
                    <Col md="12">
                        <Card>
                            <CardHeader>
                                <Row>
                                    <Col md="6">
                                        <CardTitle tag="h4">
                                            Report #{selectedReport.id}
                                            {selectedReport.is_confidential && (
                                                <Badge color="danger" className="ml-2">
                                                    CONFIDENTIAL
                                                </Badge>
                                            )}
                                        </CardTitle>
                                        <p className="card-category">{selectedReport.report_title}</p>
                                    </Col>
                                    <Col md="6" className="text-right">
                                        <Button color="secondary" size="sm" onClick={handleBackToList}>
                                            ← Back to List
                                        </Button>
                                    </Col>
                                </Row>
                            </CardHeader>
                            <CardBody>
                                <Row className="mb-3">
                                    <Col md="6">
                                        <p className="mb-2">
                                            <strong>Employee:</strong> {selectedReport.employee_name}
                                        </p>
                                        <p className="mb-2">
                                            <strong>Employee ID:</strong> {selectedReport.employee_id}
                                        </p>
                                        <p className="mb-2">
                                            <strong>Department:</strong>{" "}
                                            {selectedReport.employee_id === "MBTI2024837"
                                                ? "EPA"
                                                : selectedReport.employee_id === "MBTI2024999"
                                                    ? "Management"
                                                    : "Various"}
                                        </p>
                                    </Col>
                                    <Col md="6">
                                        <p className="mb-2">
                                            <strong>Submitted:</strong>{" "}
                                            {new Date(selectedReport.submitted_at).toLocaleString()}
                                        </p>
                                        <p className="mb-2">
                                            <strong>Status:</strong>{" "}
                                            <Badge
                                                color={
                                                    selectedReport.status === "approved" ? "success" : "warning"
                                                }
                                            >
                                                {selectedReport.status}
                                            </Badge>
                                        </p>
                                    </Col>
                                </Row>

                                {selectedReport.is_confidential && (
                                    <Alert color="danger">
                                        <strong>⚠️ CONFIDENTIAL REPORT</strong>
                                        <br />
                                        This report contains sensitive information. Unauthorized access or
                                        distribution is prohibited.
                                    </Alert>
                                )}

                                <Card className="mb-3">
                                    <CardBody
                                        style={{
                                            backgroundColor: "#f8f9fa",
                                            maxHeight: "500px",
                                            overflowY: "auto",
                                            whiteSpace: "pre-wrap",
                                            fontFamily: "monospace",
                                            fontSize: "13px",
                                            lineHeight: "1.6",
                                        }}
                                    >
                                        {selectedReport.report_content}
                                    </CardBody>
                                </Card>

                                <Row>
                                    <Col className="text-center">
                                        <Button
                                            color="primary"
                                            onClick={handlePreviousReport}
                                            disabled={parseInt(reportId) <= 1}
                                            className="mr-2"
                                        >
                                            ← Previous Report (#{parseInt(reportId) - 1})
                                        </Button>
                                        <Button
                                            color="primary"
                                            onClick={handleNextReport}
                                            disabled={parseInt(reportId) >= 512}
                                        >
                                            Next Report (#{parseInt(reportId) + 1}) →
                                        </Button>
                                    </Col>
                                </Row>

                                <Row className="mt-3">
                                    <Col className="text-center">
                                        <small className="text-muted">
                                            Viewing report {reportId} of 512 total reports
                                        </small>
                                    </Col>
                                </Row>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </div>
        );
    }

    // Show report not found
    if (reportId && !selectedReport) {
        return (
            <div className="content">
                <Row>
                    <Col md="12">
                        <Card>
                            <CardBody className="text-center">
                                <h4>Report Not Found</h4>
                                <p className="text-muted">Report #{reportId} does not exist.</p>
                                <Button color="primary" onClick={handleBackToList}>
                                    Back to Reports List
                                </Button>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </div>
        );
    }

    // Show reports list
    return (
        <>
            <div className="content">
                <Row>
                    <Col md="12">
                        <Card>
                            <CardHeader>
                                <CardTitle tag="h4">Weekly Reports Archive</CardTitle>
                                <p className="card-category">
                                    Browse submitted weekly reports from team members
                                </p>
                            </CardHeader>
                            <CardBody>
                                <Row className="mb-3">
                                    <Col md="12" className="text-right">
                                        <span className="text-muted">
                                            Page {currentPage} of {totalPages} • Total: 512 reports
                                        </span>
                                    </Col>
                                </Row>

                                <Table responsive hover>
                                    <thead className="text-primary">
                                        <tr>
                                            <th style={{ width: "80px" }}>ID</th>
                                            <th>Report Title</th>
                                            <th style={{ width: "180px" }}>Employee</th>
                                            <th style={{ width: "150px" }}>Date</th>
                                            <th style={{ width: "100px" }}>Status</th>
                                            <th style={{ width: "100px" }}>Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {reports.map((report) => (
                                            <tr key={report.id}>
                                                <td>
                                                    <strong>#{report.id}</strong>
                                                </td>
                                                <td>{report.report_title}</td>
                                                <td>
                                                    <small className="text-muted">
                                                        {report.employee_name}
                                                    </small>
                                                </td>
                                                <td>
                                                    <small>
                                                        {new Date(report.submitted_at).toLocaleDateString()}
                                                    </small>
                                                </td>
                                                <td>
                                                    <Badge color={report.status === "approved" ? "success" : "warning"}>
                                                        {report.status}
                                                    </Badge>
                                                </td>
                                                <td>
                                                    <Button
                                                        color="primary"
                                                        size="sm"
                                                        onClick={() => handleViewReport(report.id)}
                                                    >
                                                        View
                                                    </Button>
                                                </td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </Table>

                                <Row>
                                    <Col className="text-center">
                                        <Button
                                            color="secondary"
                                            onClick={() => setCurrentPage(currentPage - 1)}
                                            disabled={currentPage === 1}
                                            className="mr-2"
                                        >
                                            Previous
                                        </Button>
                                        <Button
                                            color="secondary"
                                            onClick={() => setCurrentPage(currentPage + 1)}
                                            disabled={currentPage === totalPages}
                                        >
                                            Next
                                        </Button>
                                    </Col>
                                </Row>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </div>
        </>
    );
}

export default Reports;
